<?php require_once("php/basketclass.php");session_start();?><!DOCTYPE html><html>	<head>		<title>Book</title>		<?php require("content/headboilerplate.html"); ?> 	</head>	<body>		<?php		require("content/header.html");		require("content/bodybegin.html");		//Fill page		initDatabase();		if(!array_key_exists('id', $_GET))			reportError("Error loading book: _GET value empty.");		$bookRow = makeQuery("SELECT * from testanima where id=".$_GET['id'])[0];		if($bookRow == null)			reportError("Error: book does not exist.");		//print book:		$bookhtml = file_get_contents ("content/book.html", true);		$bookhtml = str_replace(			array("<!--INST_B_TITLE-->", "<!--INST_B_QUANTITY-->", "<!--INST_B_AUTHOR-->", "<!--INST_B_PRICE-->", "<!--INST_B_DESCRIPTION-->", "<!--INST_B_IMAGE-->"),			array($bookRow['title'], $bookRow['stock'], $bookRow['author'], $bookRow['price'], $bookRow['description1'], $bookRow['imgs']),			$bookhtml);				//Check if the user added the item to the cart.		if(array_key_exists('quantity', $_POST))		{			if(!array_key_exists('basket', $_SESSION) or ($_SESSION['basket']==null))			{				$_SESSION['basket'] = new Basket();			}						$_SESSION['basket']->addItem($bookRow['id'], $_POST['quantity']);		}		echo $bookhtml;				require("content/bodyend.html");		require("content/footer.html");		?>	</body></html>