<?php session_start() ?><!DOCTYPE html><html>	<head>		<title>Error</title>		<?php require("content/headboilerplate.html"); ?> 	</head>	<body>		<?php		require("content/header.html");		require("content/bodybegin.html");		///////////////////////////////////////////////////////////////////////////////////////////////////////		/*		* If an error is in memory, print it. Otherwise, print that an unknown error has occurred.		*/		$errorhtml = file_get_contents ("content/error.html", true);		if(array_key_exists('error', $_SESSION))		{			$errorhtml = str_replace("<!--INST_E_ERROR-->", $_SESSION['error'], $errorhtml);		}		else		{			$errorhtml = str_replace("<!--INST_E_ERROR-->", "An unknown error has occurred.", $errorhtml);		}				echo $errorhtml;		///////////////////////////////////////////////////////////////////////////////////////////////////////		require("content/bodyend.html");		require("content/footer.html");		?>	</body></html>