<?php session_start() ?><!DOCTYPE html><html>	<head>		<title>Search</title>		<?php require("content/headboilerplate.html"); ?> 	</head>	<body>		<?php		require("content/header.html");		require("content/bodybegin.html");		///////////////////////////////////////////////////////////////////////////////////////////////////////		initDatabase();		$sqlRows = makeQuery("SELECT * from testanima");		if($sqlRows == null)		{			echo "There are no search results that match your query.";		}		else		{			$searchEntryHtml = file_get_contents ("content/singlesearchresult.html", true);			$collectiveSearchEntries = "";			foreach ($sqlRows as $row)			{				$collectiveSearchEntries = $collectiveSearchEntries . populateSearchEntryHtml($row, $searchEntryHtml);			}			$searchHtml = file_get_contents("content/search.html", true);			$searchHtml = str_replace("<!--INST_S_RESULTLIST-->", $collectiveSearchEntries, $searchHtml);			echo $searchHtml;		}		///////////////////////////////////////////////////////////////////////////////////////////////////////		require("content/bodyend.html");		require("content/footer.html");		?>	</body></html>